		if(!sap.ui.Device.system.phone){
			overwritePopover(oInput._oSuggestionPopup, oInput);
		}

		oInput._oList = new sap.m.List(oInput.getId() + "-popup-list", {
			width: "100%",
			showNoData: false
		});

