	rm.write(">");

	renderDelimiter(rm, oRoadMap, true);

	rm.write("<ul");
	rm.writeAttribute("id", oRoadMap.getId() + "-steparea");
	rm.addClass("sapUiRoadMapStepArea");
	rm.writeClasses();

