	var oMenuItem = _getMenuItem(this, oEvent);
	if(oMenuItem === "ovrflw"){
		this._bOvrFlwMnuSkipOpen = this.oOvrFlwMnu && this.oOvrFlwMnu.bOpen;
	}else if(oMenuItem){
		var oMenu = oMenuItem.getSubmenu();
		oMenuItem._bSkipOpen = oMenu && oMenu.bOpen;
	}
